import { useState } from "react";

import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";

const GET_WORK_CV = gql`
  {
    works(orderBy: id_DESC) {
      description
      location
      position
      title
      tenure
    }
  }
`;

function WorkExperience() {
  const { loading, error, data } = useQuery(GET_WORK_CV);
  const repeatTimes = 2;
  const [numWork, setNumWork] = useState(3);

  if (error) return `Error ${error.message}`;
  if (loading) {
    return (
      <div className="work-experience group">
        <div className="heading">
          <h2 className="text-center">Work Experience</h2>
        </div>

        {Array.apply(null, { length: repeatTimes }).map((e, i) => (
          <div key={i}>
            <div className="ph-item">
              <div className="ph-col-12">
                <div className="ph-row">
                  <div className="ph-col-2 big"></div>
                  <div className="ph-col-8 empty"></div>
                  <div className="ph-col-2"></div>
                  <div className="ph-col-2"></div>
                </div>
              </div>
              <div className="ph-col-12">
                <div className="ph-row">
                  <div className="ph-col-12"></div>
                  <div className="ph-col-12"></div>
                  <div className="ph-col-2"></div>
                  <div className="ph-col-10 empty"></div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  return (
    <div className="work-experience group">
      <div className="heading">
        <h2 className="text-center">Work Experience</h2>
      </div>

      {data.works
        .sort(function (a, b) {
          return b.id - a.id;
        })
        .slice(0, numWork)
        .map((job) => (
          <div className="item" key={job.id}>
            <div className="row">
              <div className="col-6">
                <h3>{job.position}</h3>
                <h4 className="organization">{job.title}</h4>
                <p className="text-muted location">
                  {job.location !== null ? job.location : ""}
                </p>
              </div>
              <div className="col-md-6">
                <span className="period">{job.tenure}</span>
              </div>
              <p className="text-muted">{job.description}</p>
            </div>
          </div>
        ))}
      {numWork > data.works.length ? (
        <></>
      ) : (
        <button
          style={{ textDecoration: "underline", width: "100%" }}
          className="btn link-primary"
          onClick={() => setNumWork(numWork + 3)}
        >
          Show more
        </button>
      )}
    </div>
  );
}

export default WorkExperience;
